using UnityEngine;[RequireComponent(typeof(Collider), typeof(Rigidbody))]public class Bullet : MonoBehaviour{    [HideInInspector] public float BulletSpeed;    [HideInInspector] public int BulletDamage;    private Transform _transform;    private Rigidbody _rb;    private Collider _collider;    [SerializeField] private GameObject effect;    private void Awake()    {        _transform = GetComponent<Transform>();        _rb = GetComponent<Rigidbody>();        _collider = GetComponent<Collider>();    }    private void Start()    {
        ShotBullet();    }    private void OnTriggerEnter(Collider other)    {        if (other.gameObject.CompareTag("Enemy"))        {            GameManager.Gold += 10;            var enemy = other.gameObject.GetComponent<EnemyAi>();            enemy.TakeDamage(BulletDamage);            Instantiate(effect, other.transform.position, Quaternion.identity);            Destroy(gameObject);        }    }    private void ShotBullet()
    {
        _collider.isTrigger = true;        _rb.AddForce(_transform.up * BulletSpeed, ForceMode.Impulse);        Destroy(gameObject, 3f);
    }}
